import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Exercise = props => (
  <div class="card" style="width: 18rem;">
  <div class="card-body">
    <h5 class="card-title">{props.exercise.question}</h5>
    <h6 class="card-subtitle mb-2 text-muted">par {props.exercise.username}</h6>
    <p class="card-text">{props.exercise.reponse}</p>
    <a href="#" onClick={() => { props.deleteExercise(props.exercise._id) }}>delete</a>
    <a href="#" class="card-link"><Link to={"/Admin/"+props.exercise._id}>edit</Link></a>
  </div>
</div>
)




export default class ExercisesList extends Component {
  constructor(props) {
    super(props);

    this.deleteExercise = this.deleteExercise.bind(this)

    this.state = {exercises: []};
  }

  componentDidMount() {
    axios.get('http://localhost:5000/exercises/')
      .then(response => {
        this.setState({ exercises: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  deleteExercise(id) {
    axios.delete('http://localhost:5000/exercises/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
      exercises: this.state.exercises.filter(el => el._id !== id)
    })
  }

  exerciseList() {
    return this.state.exercises.map(currentexercise => {
      return <Exercise exercise={currentexercise} deleteExercise={this.deleteExercise} key={currentexercise._id}/>;
    })
  }

  render() {
    return(
      <div>{ this.exerciseList() }</div>
      
    )
 }
}
