import React,{useState, useEffect} from 'react'

function Graph() {
    const [data, setData] = useState(null);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const runCall =async() => {
            let apiValue = await fetchData();
            const header = apiValue.split('\n').slice(0).map(line => (line.split(',')))[0].slice(4);

            const frData = apiValue.split('\n').slice(0).map(line => (line.split(',')))[129].slice(4);
            
            const chartData =[];
            for (let i=0; i<header.length ; i++){
                const newRow ={};
                newRow[header[i]] = parseInt(frData[i]);
                chartData.push(newRow);
                console.log(chartData);
            }
        }
        const fetchData = async() => {
            const requestOption = {
                method: "GET",
                redirect: "follow"
            }
            const URL= `https://raw.githubusercontent.com/CSSEGISandData/COVID-19/master/csse_covid_19_data/csse_covid_19_time_series/time_series_covid19_confirmed_global.csv`

            try {
                const response = await fetch(URL,requestOption);
                return response.ok ? response.text() : null
            } catch (err) {
                console.log(err);
                return null;
            }
        }
        runCall();
    },[])
        return(
            <div>
                <h1>Graph</h1>
            </div>
        );
    
}export default Graph;
