import React, { Component, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const items =[
  {
  title:'Comment créer un Dashboard',
  content: 'Il suffit de suivre les cours de Web'
  },
  {
  title: 'Winamax ou Betclic ?',
  content: 'Ca dépend, je peux avoir un freebet sur Winamax ? ;)'
  },
  {
  title: 'OCRES ou SI',
  content: 'La question ne se pose même pas'
  },
];

const Exercise = props => (
  <tr>
    <td>{props.exercise.username}</td>
    <td>{props.exercise.question}</td>
    <td>{props.exercise.duration}</td>
    <td>{props.exercise.date.substring(0,10)}</td>
    <td>
      <Link to={"/edit/"+props.exercise._id}>edit</Link> | <a href="#" onClick={() => { props.deleteExercise(props.exercise._id) }}>delete</a>
    </td>
  </tr>
)




export default class ExercisesList extends Component {
  constructor(props) {
    super(props);

    this.deleteExercise = this.deleteExercise.bind(this)

    this.state = {exercises: []};
  }

  componentDidMount() {
    axios.get('http://localhost:3000/exercises/')
      .then(response => {
        this.setState({ exercises: response.data })
      })
      .catch((error) => {
        console.log(error);
      })
  }

  deleteExercise(id) {
    axios.delete('http://localhost:3000/exercises/'+id)
      .then(response => { console.log(response.data)});

    this.setState({
      exercises: this.state.exercises.filter(el => el._id !== id)
    })
  }

  exerciseList() {
    return this.state.exercises.map(currentexercise => {
      return <Exercise exercise={currentexercise} deleteExercise={this.deleteExercise} key={currentexercise._id}/>;
    })
  }

  render() {
    return(
      <div>
      <table className="table">
        <thead className="thead-light">
          <tr>
            <th>Username</th>
            <th>Question</th>
            <th>Reponse</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          { this.exerciseList() }
        </tbody>
      </table>
    </div>
    )
 }
}
